import './App.css';
import userIcon from './images/image-jeremy.png'
import play from './images/play.svg'
import work from './images/work.svg'
import React, {useState} from 'react'
import Component from './Component';


function App() {
  const [dailySpentTime, setDailySpentTime] = useState();
  return (
    <div className="wrapper">
      <div className="left-box">
          {/* user's img and name */}
          <div className="user-report">
            <img className="user-img" src={userIcon}></img>
            <div className="user-info">
              <p className="report-p">Report for</p>
              <p className="user-name">Jeremy Robson</p>
            </div>
          </div>
          {/* div for time selection */}
          <div className="time-selection-div">
            <button onClick={() => {setDailySpentTime(dailySpentTime === 5)}} class="button-p-daily">Daily</button>
            <button class="button-p-weekly">Weekly</button>
            <button class="button-p-monthly">Monthly</button>
          </div>
      </div>

      <div className="right-box">
        <Component 
          activity='Work' 
          image={work} 
          className='activity-card-background-orange'
          dailyValue='5'
        />
        <Component 
          activity='Play' 
          image={play} 
          className='activity-card-background-sky-blue'
          dailyValue='1'
        />
      </div>
    </div>
  );
}

export default App;

