import React, { useState } from 'react';
import Component from './Component';
import './App.css';
import userIcon from './images/image-jeremy.png';
import play from './images/play.svg';
import work from './images/work.svg';
import study from './images/stody.svg';
import exercise from './images/exercise.svg';
import social from './images/social.svg';
import selfCare from './images/self-care.svg';

const initialActivityState = {
  daily: '0',
  weekly: '0',
  monthly: '0',
};

const activities = {
  work: { image: work, className: 'activity-card-background-orange' },
  play: { image: play, className: 'activity-card-background-sky-blue' },
  study: { image: study, className: 'activity-card-background-pink' },
  exercise: { image: exercise, className: 'activity-card-background-green' },
  social: { image: social, className: 'activity-card-background-violet' },
  selfCare: { image: selfCare, className: 'activity-card-background-yellow' },
};

const timeValues = {
  daily: { work: '5', play: '1', study: '0', exercise: '1', social: '1', selfCare: '0' },
  weekly: { work: '32', play: '10', study: '4', exercise: '4', social: '5', selfCare: '2' },
  monthly: { work: '103', play: '23', study: '13', exercise: '11', social: '21', selfCare: '7' },
};

function App() {
  const [workTime, setWorkTime] = useState({ ...initialActivityState });
  const [playTime, setPlayTime] = useState({ ...initialActivityState });
  const [studyTime, setStudyTime] = useState({ ...initialActivityState });
  const [exerciseTime, setExerciseTime] = useState({ ...initialActivityState });
  const [socialTime, setSocialTime] = useState({ ...initialActivityState });
  const [selfCareTime, setSelfCareTime] = useState({ ...initialActivityState });

  const [activeButton, setActiveButton] = useState(null);

  const handleButtonClick = (button) => {
    setActiveButton(button);

    const updateState = (activity, values) => {
      activity((prevActivity) => ({
        daily: values.daily || prevActivity.daily,
        weekly: values.weekly || prevActivity.weekly,
        monthly: values.monthly || prevActivity.monthly,
      }));
    };

    updateState(setWorkTime, timeValues[button].work);
    updateState(setPlayTime, timeValues[button].play);
    updateState(setStudyTime, timeValues[button].study);
    updateState(setExerciseTime, timeValues[button].exercise);
    updateState(setSocialTime, timeValues[button].social);
    updateState(setSelfCareTime, timeValues[button].selfCare);
  };

  return (
    <div className="wrapper">
      <div className="left-box">
        <div className="user-report">
          <img className="user-img" src={userIcon} alt="User"></img>
          <div className="user-info">
            <p className="report-p">Report for</p>
            <p className="user-name">Jeremy Robson</p>
          </div>
        </div>
        <div className="time-selection-div">
          <button onClick={() => handleButtonClick('daily')} className="button-p-daily">
            Daily
          </button>
          <button onClick={() => handleButtonClick('weekly')} className="button-p-weekly">
            Weekly
          </button>
          <button onClick={() => handleButtonClick('monthly')} className="button-p-monthly">
            Monthly
          </button>
        </div>
      </div>

      <div className="right-box">
        {Object.entries(activities).map(([activity, { image, className }]) => (
          <Component
            key={activity}
            activity={activity.charAt(0).toUpperCase() + activity.slice(1)}
            image={image}
            className={className}
            dailyValue={workTime[activity].daily}
            weeklyValue={workTime[activity].weekly}
            monthlyValue={workTime[activity].monthly}
            activeButton={activeButton}
          />
        ))}
      </div>
    </div>
  );
}

export default App;
