import './App.css';
import userIcon from './images/image-jeremy.png'
import play from './images/play.svg'
import work from './images/work.svg'
import React, {useState, useEffect} from 'react'
import Component from './Component';


function App() {
  const [dailySpentTime, setDailySpentTime] = useState('0');
  const [weeklySpentTime, setWeeklySpentTime] = useState('0');
  const [monthlySpentTime, setMonthlySpentTime] = useState('0');
  const [activeButton, setActiveButton] = useState(null);
  const handleButtonClick = (button) => {
    setActiveButton(button);

    // Set different values based on the button clicked
    if (button === 'daily') {
      setDailyWTime('5'); // Set daily work time to 5 hours
      setDailyPlayTime('10'); // Set daily play time to 10 hours
    } else {
      // Reset other time values if a different button is clicked
      setDailyWorkTime('0');
      setDailyPlayTime('0');
    }
  };

  return (

    <div className="wrapper">
      <div className="left-box">
        {/* user's img and name */}
        <div className="user-report">
          <img className="user-img" src={userIcon}></img>
          <div className="user-info">
            <p className="report-p">Report for</p>
            <p className="user-name">Jeremy Robson</p>
          </div>
        </div>
        {/* div for time selection */}
        <div className="time-selection-div">
          <button onClick={() => 
            handleButtonClick('daily')}
            class="button-p-daily">Daily
          </button>
          <button onClick={() => {
            handleButtonClick('weekly')}} 
            class="button-p-weekly">Weekly
          </button>
          <button onClick={() => 
            handleButtonClick('monthly')}
            class="button-p-monthly">Monthly
          </button>
        </div>
      </div>

      <div className="right-box">
        <Component
          activity='Work'
          image={work}
          className='activity-card-background-orange'
          dailyValue={dailySpentTime}
          weeklyValue={weeklySpentTime}
          monthlyValue={monthlySpentTime}
          activeButton={activeButton}
        />
        <Component 
          activity='Play' 
          image={play} 
          className='activity-card-background-sky-blue'
          dailyValue={dailySpentTime}
          weeklyValue={weeklySpentTime}
          monthlyValue={monthlySpentTime}
          activeButton={activeButton}
        />
      </div>
    </div>
  );
}

export default App;
